sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    // Add your code here

	var grupo = null;
    var grUser = new GlideRecord("sys_user_grmember");
    grUser.addEncodedQuery('user=' + current.getValue('assigned_to'));
    grUser.query();

    if (grUser.next()) {
        grupo = grUser.getValue('group');

    }

    var gr = new GlideRecord("incident_task");
    gr.initialize();
    gr.addActiveQuery(); // Ã© igual a gr.addQuery('active',true);
    gr.addQuery('incident', current.getUniqueValue());
    gr.addQuery('assigned_to', current.getValue('assigned_to'));
    gr.query();
    if (!gr.next()) {
        gr.setValue('short_description', current.getValue('short_description'));
        gr.setValue('description', current.getValue('description'));
        gr.setValue('incident', current.getUniqueValue());
        gr.setValue('assigned_to', current.getValue('assigned_to'));
        gr.setValue('assignment_group', grupo);
        gr.insert();
    } else {
        gs.addInfoMessage("Ja existe uma task para esta pessoa");
        if (gr.getValue('priority') != 1) {
            gr.setValue('priority', 1);
            gs.addInfoMessage("a prioridade ja foi elevada");
        }

        gr.update();
    }

})(current, previous);
